    private void jButton_loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_loginActionPerformed
        // TODO add your handling code here
        final String role = "Admin";
        String uname = jTextField_username.getText();
        String pword = jPasswordField_password.getText();
        Login log = new Login(uname, pword, role);

        if (log.checkAvailability()) {
                CreateAccount creAcc = new CreateAccount();
                creAcc.setVisible(true);
                this.dispose();
        } else {
            JOptionPane.showMessageDialog(null, "Invalid Username/Password/Role");
        }

    }//GEN-LAST:event_jButton_loginActionPerformed

